import React from "react";

class NewProjectForm extends React.Component {
    constructor (props) {
        super(props)
        this.state={
            project: ""
        }
        this.sendFormNewProject = this.sendFormNewProject.bind(this);
    }

    valid (item, type) {
        this.setState({project: item.target.value});
    }

    sendFormNewProject(e) {
        e.preventDefault();
        console.warn(this.state.project);
        for(let i = 0; i < 99999999; i++) {
            console.log(1);
        }
    }
    render = () => {
        return (
            <form id="form-send-project" name="form-send-project" onSubmit={()=>this.sendFormNewProject}>
                <input type="text" onChange={(item)=>this.valid(item, "project")} className="form-add-input" id="new-project" name="newproject" autoComplete="off" /><br />
                <input type="submit" className="red-button-accept form-add-buttons" id="send-project" name="send-project" value="Додати" />
                <input type="reset" className="button-cancel form-add-buttons" id="reset-project" value="Скасувати" />
            </form>
        );
    }
}

export default NewProjectForm;
