import React from "react";
import axios from 'axios';

class ChangeProject extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            newNameProject: "",
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange = (event) => {
        this.setState({ newNameProject: event.target.value });
    }

    handleSubmit = async (event) => {
        event.preventDefault();
        console.warn('Змінити проект: ' + this.props.element.project + " на " + this.state.newNameProject);
        await axios.post(`http://localhost:3001/change-project`, {
            id_project: this.props.element.id_project,
            project: this.state.newNameProject
        }).then(() => {
            this.props.showChangeProjectForm();
            this.props.getList();
        })
    }

    render = () => {
        return (
            <form id="form-change-project" name="form-change-project" onSubmit={this.handleSubmit}>
                <input type="text" value={this.state.newNameProject} onChange={this.handleChange} />
                <input type="submit" value="Змінити" />
                <input type="reset" onClick={this.props.showChangeProjectForm} value="Скасувати" />
            </form>
        );
    }
}

export default ChangeProject;
