import React from "react";
import axios from 'axios';

class ChangeProject extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            newNameProject: "",
        }
    }

    handleChange = (event) => {
        this.setState({ project: event.target.value });
    }

    handleSubmit = (event) => {
        event.preventDefault();
        console.warn('Змінити проект: ' + this.props.element.project + " на " + this.state.newNameProject);
        axios.post(`http://localhost:3001/change-project`, {
            id_project: this.props.element.id_project, 
            newNameProject: this.state.newNameProject
        });
        this.props.getList();
    }

    render = () => {
        return (
            <form id="form-change-project" name="form-change-project" onSubmit={this.handleSubmit}>
                <input  type="text" value={this.state.newNameProject} onChange={this.handleChange}  />
                <input type="submit" value="Змінити"/>
                <input type="reset" value="Скасувати"/>
            </form>
        );
    }
}

export default  ChangeProject;
