import React from "react";

export default class ItemProject extends React.Component {
    constructor (props) {
        super(props);
        this.state = {

        }

    }

    removeProject = (event) => {
            event.preventDefault();
            console.warn('Відправлений проект: ' + this.state.project);
            axios.post(`http://localhost:3001/removeproject`, {
                newproject: this.state.project
            })
            // this.props.getList();
            this.clearForm();
    }

    render = () => {
        return (
            <li className="projects-list" id={"project-id-" + this.props.element.id_project}>
                <span className="menu-span" id={"id-project-" + this.props.element.id_project}>  {this.props.element.project}  </span>
                <b id={"remove-project-button-" + this.props.element.id_project} className="item-delete-button">X</b>
                <b id={"change-project-button-" + this.props.element.id_project} className="item-change-button">✎</b>
            </li>
        );
    }
}
