import React from "react";

export default class ItemProject extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
           showButtonsBlock: false 
        }
    }

    toggleVisibilityBlock = () => {
        if (this.state.showButtonsBlock) {
            this.setState({showButtonsBlock: false})
        } else {
            this.setState({showButtonsBlock: true})
        }
    }

    render = () => {
        return (
            <li className="projects-list" id={"project-id-" + this.props.element.id_project}>
                <span className="menu-span" id={"id-project-" + this.props.element.id_project}>  {this.props.element.project}  </span>
                <b id={"three-points-button-project-" + this.props.element.id_project} onClick={this.toggleVisibilityBlock} className="three-points-button">···</b>
                { this.state.showButtonsBlock ?
                <div className="buttons-block" id={"buttons-block-project-" + this.props.element.id_project}>
                    <b id={"change-project-button-" + this.props.element.id_project} className="item-change-button">✎ Редагувати</b>
                    <b id={"remove-project-button-" + this.props.element.id_project} className="item-delete-button">X Видалити</b>
                </div>
                : none}
            </li>
        );
    }
}
